                    UnlockHiddenPresets.IsEnabled = true;
                    FixThumbnails.IsEnabled = true;
                    ShowTrafficHSNull.IsEnabled = true;
                    ClearGarage.IsEnabled = true;
                }
            }
            #endregion
        }

